import React, { MouseEvent } from 'react';
import { connect } from 'react-redux';
import { Grid, Button, Typography, Icon } from '@material-ui/core';


class CreateProfile extends React.Component{
    state = {music: [], bio: null, location: null, selectedFemale: "", selectedMale: "" }
    selectGender = (gender: string) => {
        if (this.state[gender] === 'selected')
            this.setState({ gender: "" });
    }
    render() {
        return (
            <div>
                <Typography align="center" variant="h3" gutterBottom>
                    Tell us more about you
                    </Typography>
                <Grid container justify="center">
                    <Grid item>
                        <Button onClick={(e)=> this.selectGender("selectedFemale")} className={`${this.state.selectedFemale}`} size="large"><Icon className="fas fa-female" color="secondary" fontSize="large"/></Button>
                        <Button className={`${this.state.selected}`} size="large"><Icon className="fas fa-male" color="secondary" fontSize="large"/></Button>
                    </Grid>
            </Grid>
                </div>
        );
    }
}

const mapStateToProps = (state: object) => {
    return state
}

export default connect(mapStateToProps)(CreateProfile);
