import api from '../api';
import { Action } from 'redux';
import { userAuth, LOGIN } from './types';
import { ThunkAction } from 'redux-thunk';

export const loginUser = (user: userAuth): ThunkAction<any, any, any, Action> => dispatch => {
    const { password, username } = user;
    const result = api.post('/user/login', {
        password,
        username
    });
    dispatch ({
        type: LOGIN,
        payload: result
    })
}
