import React, { MouseEvent } from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import { Menu, MenuItem } from '@material-ui/core';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import { Link } from 'react-router-dom';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      flexGrow: 1,
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    title: {
      flexGrow: 1,
    },
  }),
);

class NavBar extends React.Component{
    state = {dropdown: false}
    classes = useStyles();

  handleClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    this.setState({dropdown: event.currentTarget});
  };

    handleClose = (e: MouseEvent) => {
        e.preventDefault();
        this.setState({ dropdown: null });
  };
    render() {
        return (
            <div className={classes.root}>
                <AppBar position="static">
                    <Toolbar>
                        <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu"></IconButton>
                        <Typography variant="h6" className={classes.title}>
                            News
          </Typography>
                        <Button color="inherit" onClick={handleClick}>Login</Button>
                        <Menu
                            id="simple-menu"
                            anchorEl={anchorEl}
                            open={Boolean(anchorEl)}
                            onClose={handleClose}
                        >
                            <MenuItem onClick={(e) => this.handleClose(e)}>Profile</MenuItem>
                            <MenuItem onClick={(e) => this.handleClose(e)}>My account</MenuItem>
                            <MenuItem onClick={(e) => this.handleClose(e)}>Logout</MenuItem>
                        </Menu>
                    </Toolbar>
                </AppBar>
            </div>
        );
    }
}
export default NavBar;
