import React, { MouseEvent } from 'react';
import { connect } from 'react-redux';
import { Grid, Button } from '@material-ui/core';


class CreateProfile extends React.Component{
    state = { gender: null, music: [], bio: null, location: null, selected: "" }
    selectHobby = (hobby: string, e: MouseEvent) => {
        this.setState({ selected: "selected" });
        console.log(this.state)
    }
    render() {
        return (
            <div>
            <Grid container justify="center">
                    <Grid item>
                        <Button>Rock</Button>
                        <Button>Ruski</Button>
                        </Grid>
            </Grid>
            <Grid container justify="center">
                <Grid xs={3} item>
                        <Button >Tets</Button>
                        <br/>
                        <Button>Svod</Button>
                    </Grid>
                    <Grid xs={3} item container alignItems="flex-end" direction="column">
                        <Button >Krug</Button>
                        <button className={`primary ${this.state.selected}`} id="Sud" onClick={(e) =>this.selectHobby("Sud", e)}>Sud</button>
                    </Grid>
                </Grid>
                </div>
        );
    }
}

const mapStateToProps = (state: object) => {
    return state
}

export default connect(mapStateToProps)(CreateProfile);
