import api from '../api';
import { Action } from 'redux';
import { userAuth, LOGIN, LOGOUT } from './types';
import { ThunkAction } from 'redux-thunk';

export const loginUser = (user: userAuth): ThunkAction<any, any, any, Action> => async (dispatch) => {
    const { password, username } = user;
    const token = await api.post('/auth/register', {
        password,
        username
    });
    console.log(token);
    // localStorage.setItem('tinderbae-token', token.toString())
    dispatch ({
        type: LOGIN
    })
}

export const logOutUser = () => {
    localStorage.removeItem('tibderbae-token');
    return ({
        type: LOGOUT
    })
}
