import React from 'react';
import { TextField,Grid, Button, Typography } from '@material-ui/core';

export default class HobbyList extends React.Component{
    state = { arrList: [], hobby: ""};
    addToList = (e: any) => {
        e.preventDefault();
        this.setState({
            arrList: [...this.state.arrList, this.state.hobby],
            hobby: ""
        });
    }
    render() {
        const ItemList = this.state.arrList.map((item) => {
            return (
                <div>
                    <Typography>{item}</Typography>
                    </div>
            )
        })
        return (
            <div>
                <Grid container justify="center">
                    <form>
                        <TextField id="standard-basic" onChange={(e) => this.setState({hobby: e.target.value})} label="Type your hobbies" />
                        <Button type="submit" onClick={(e) => { this.addToList(e) }}>Add</Button>
                    </form>
                </Grid>
                <Grid container justify="center">
                {ItemList}
                </Grid>
            </div>
        )
    }
}
