import React, {useState} from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import { Menu, MenuItem } from '@material-ui/core';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';


const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      flexGrow: 1,
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    title: {
      flexGrow: 1,
    },
  }),
);

class NavBar extends React.Component{
    state = { dropdown: null };
    classes = useStyles();

    handleClick = (event: React.MouseEvent<HTMLButtonElement>) => {
        this.setState({ dropdown: event.currentTarget });
  };

    handleClose = () => {
        this.setState({ dropdown: null });
    };
    render() {
        return (
            <div className={this.classes.root}>
                <AppBar position="static">
                    <Toolbar>
                        <IconButton edge="start" className={this.classes.menuButton} color="inherit" aria-label="menu"></IconButton>
                        <Typography variant="h6" className={this.classes.title}>
                            News
          </Typography>
                        <Button color="inherit" onClick={(e) => this.handleClick(e)}>Login</Button>
                        <Menu
                            id="simple-menu"
                            anchorEl={this.state.dropdown}
                            open={Boolean(this.state.dropdown)}
                        >
                            <MenuItem onClick={this.handleClose}>Profile</MenuItem>
                            <MenuItem onClick={this.handleClose}>My account</MenuItem>
                            <MenuItem onClick={this.handleClose}>Logout</MenuItem>
                        </Menu>
                    </Toolbar>
                </AppBar>
            </div>
        );
    }
}
export default NavBar;
